#### Preamble ####
# Purpose: This file gets Australian Hansard PDF files from the parliament website for the dates that they only make PDFs available.
# Author: Rohan Alexander
# Email: rohan.alexander@anu.edu.au
# Last updated: 17 August 2018
# Prerequisites: 
# To do:


#### Set up workspace ####
# Load libraries
library(tidyverse)
# update.packages()


#### Scraping ####
the_dates <- c() # Add whatever dates

for (i in seq_along(the_dates)) {
  the_URL <-
    paste0(
      "http://parlinfo.aph.gov.au/parlInfo/download/chamber/hansardr/",
      the_dates[i],
      "/toc_pdf/H%20",
      the_dates[i],
      ".pdf;fileType=application/pdf"
    )
  
  save_here <- paste0("data/PDFs/", the_dates[i], ".pdf")
  
  download.file(the_URL, save_here)
  
  print(paste(
    "Done with file",
    i,
    "of",
    length(the_dates),
    "-",
    the_dates[i],
    "-",
    "at",
    Sys.time()
  ))  # Helpful so that you know progress when running it on all the records
  
  Sys.sleep(30) # Space out requests by 30 seconds
  
}


